import React from 'react';
import { motion } from 'framer-motion';
import { ChevronDown, Eye, Database, Cloud } from 'lucide-react';

const HeroSection = () => {
  const floatingIcons = [
    { Icon: Eye, delay: 0, x: 100, y: 50 },
    { Icon: Database, delay: 1, x: -80, y: 80 },
    { Icon: Cloud, delay: 2, x: 120, y: -60 },
  ];

  return (
    <section id="hero" className="min-h-screen flex items-center justify-center relative overflow-hidden pt-16">
      {/* Floating Background Elements */}
      {floatingIcons.map(({ Icon, delay, x, y }, index) => (
        <motion.div
          key={index}
          initial={{ opacity: 0, scale: 0 }}
          animate={{ 
            opacity: 0.1, 
            scale: 1,
            x: [0, x, 0],
            y: [0, y, 0]
          }}
          transition={{ 
            duration: 8,
            delay,
            repeat: Infinity,
            repeatType: "reverse"
          }}
          className="absolute text-cyber-blue"
        >
          <Icon size={80} />
        </motion.div>
      ))}

      <div className="text-center z-10 max-w-6xl mx-auto px-4">
        <motion.h1
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1 }}
          className="text-6xl md:text-8xl font-black mb-6 glow-text"
        >
          THE INTERNET
          <br />
          <span className="text-cyber-pink">NEVER FORGETS</span>
        </motion.h1>

        <motion.p
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1, delay: 0.5 }}
          className="text-xl md:text-2xl mb-12 text-gray-300 max-w-3xl mx-auto"
        >
          Explore how your digital footprint lives forever in the vast network of servers, 
          caches, and archives that make up the modern internet.
        </motion.p>

        <motion.div
          initial={{ opacity: 0, scale: 0.8 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 1, delay: 1 }}
          className="flex flex-col sm:flex-row gap-6 justify-center items-center mb-16"
        >
          <motion.button
            whileHover={{ scale: 1.05, boxShadow: "0 0 30px rgba(0, 245, 255, 0.5)" }}
            whileTap={{ scale: 0.95 }}
            className="px-8 py-4 bg-gradient-to-r from-cyber-blue to-cyber-purple rounded-lg font-bold text-black hover-glow"
          >
            Start Exploring
          </motion.button>

          <motion.button
            whileHover={{ scale: 1.05 }}
            className="px-8 py-4 border border-cyber-blue rounded-lg font-bold text-cyber-blue hover:bg-cyber-blue hover:text-black transition-all"
          >
            Learn More
          </motion.button>
        </motion.div>

        <motion.div
          animate={{ y: [0, 10, 0] }}
          transition={{ duration: 2, repeat: Infinity }}
          className="text-cyber-blue cursor-pointer"
        >
          <ChevronDown size={40} />
        </motion.div>
      </div>

      {/* Binary Rain Effect */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        {Array.from({ length: 20 }).map((_, i) => (
          <motion.div
            key={i}
            initial={{ y: -100, opacity: 0 }}
            animate={{ y: window.innerHeight + 100, opacity: [0, 1, 0] }}
            transition={{
              duration: Math.random() * 3 + 2,
              repeat: Infinity,
              delay: Math.random() * 5,
            }}
            className="absolute text-cyber-blue text-xs"
            style={{ left: `${Math.random() * 100}%` }}
          >
            {Math.random() > 0.5 ? '1' : '0'}
          </motion.div>
        ))}
      </div>
    </section>
  );
};

export default HeroSection;
