import React from 'react';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import { User, Server, Cloud, Search, Archive } from 'lucide-react';

const DataStorageSection = () => {
  const [ref, inView] = useInView({ threshold: 0.3, triggerOnce: true });

  const steps = [
    {
      icon: User,
      title: "User Posts Data",
      description: "Every post, photo, comment, and interaction creates a digital trace",
      color: "text-cyber-pink"
    },
    {
      icon: Server,
      title: "Stored on Servers",
      description: "Data is immediately stored across multiple server networks worldwide",
      color: "text-cyber-blue"
    },
    {
      icon: Cloud,
      title: "Cloud Backup",
      description: "Automatic replication ensures data survives hardware failures",
      color: "text-cyber-purple"
    },
    {
      icon: Search,
      title: "Search Engine Indexing",
      description: "Crawlers continuously scan and index content for search results",
      color: "text-green-400"
    },
    {
      icon: Archive,
      title: "Archive Bots",
      description: "Wayback Machine and other services create permanent snapshots",
      color: "text-yellow-400"
    }
  ];

  return (
    <section id="storage" className="py-20 px-4">
      <div className="max-w-7xl mx-auto">
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 1 }}
          className="text-center mb-16"
        >
          <h2 className="text-5xl font-bold mb-6 glow-text">
            How the Internet <span className="text-cyber-pink">Stores</span> Information
          </h2>
          <p className="text-xl text-gray-300 max-w-3xl mx-auto">
            Follow the journey of your data from the moment you hit "post" to its permanent residence in the digital realm.
          </p>
        </motion.div>

        <div ref={ref} className="relative">
          {/* Timeline Line */}
          <div className="absolute left-1/2 transform -translate-x-1/2 w-1 h-full bg-gradient-to-b from-cyber-blue to-cyber-pink opacity-30"></div>

          {steps.map((step, index) => (
            <motion.div
              key={index}
              initial={{ opacity: 0, x: index % 2 === 0 ? -100 : 100 }}
              animate={inView ? { opacity: 1, x: 0 } : {}}
              transition={{ duration: 0.8, delay: index * 0.3 }}
              className={`flex items-center mb-16 ${index % 2 === 0 ? 'flex-row' : 'flex-row-reverse'}`}
            >
              <div className={`w-1/2 ${index % 2 === 0 ? 'pr-8 text-right' : 'pl-8'}`}>
                <motion.div
                  whileHover={{ scale: 1.05 }}
                  className="bg-dark-card p-6 rounded-lg cyber-border hover-glow"
                >
                  <div className={`flex items-center gap-4 ${index % 2 === 0 ? 'justify-end' : 'justify-start'}`}>
                    <step.icon className={`${step.color} w-8 h-8`} />
                    <h3 className="text-2xl font-bold">{step.title}</h3>
                  </div>
                  <p className="text-gray-300 mt-4">{step.description}</p>
                </motion.div>
              </div>

              {/* Timeline Node */}
              <motion.div
                initial={{ scale: 0 }}
                animate={inView ? { scale: 1 } : {}}
                transition={{ duration: 0.5, delay: index * 0.3 + 0.5 }}
                className="w-4 h-4 bg-cyber-blue rounded-full border-4 border-dark-bg z-10 relative"
              >
                <div className="absolute inset-0 bg-cyber-blue rounded-full animate-ping"></div>
              </motion.div>

              <div className="w-1/2"></div>
            </motion.div>
          ))}
        </div>

        {/* Data Flow Animation */}
        <motion.div
          initial={{ opacity: 0 }}
          animate={inView ? { opacity: 1 } : {}}
          transition={{ duration: 2, delay: 2 }}
          className="mt-16 text-center"
        >
          <div className="flex justify-center items-center space-x-8 flex-wrap">
            {['Your Device', 'ISP', 'CDN', 'Server Farm', 'Cloud Storage'].map((node, index) => (
              <motion.div
                key={node}
                animate={{ 
                  scale: [1, 1.1, 1],
                  boxShadow: [
                    '0 0 0px rgba(0, 245, 255, 0)',
                    '0 0 20px rgba(0, 245, 255, 0.5)',
                    '0 0 0px rgba(0, 245, 255, 0)'
                  ]
                }}
                transition={{ 
                  duration: 2,
                  delay: index * 0.4,
                  repeat: Infinity,
                  repeatDelay: 3
                }}
                className="bg-dark-card px-4 py-2 rounded-lg cyber-border text-sm"
              >
                {node}
              </motion.div>
            ))}
          </div>
        </motion.div>
      </div>
    </section>
  );
};

export default DataStorageSection;
