import React from 'react';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import { Search, Database, Archive, Globe } from 'lucide-react';

const DataRetrievalSection = () => {
  const [ref, inView] = useInView({ threshold: 0.3, triggerOnce: true });

  return (
    <section id="retrieval" className="py-20 px-4 bg-gradient-to-b from-dark-bg to-gray-900">
      <div className="max-w-7xl mx-auto">
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          className="text-center mb-16"
        >
          <h2 className="text-5xl font-bold mb-6 glow-text">
            How Data is <span className="text-cyber-blue">Retrieved</span>
          </h2>
          <p className="text-xl text-gray-300">Even deleted content can resurface from the digital depths</p>
        </motion.div>

        <div ref={ref} className="grid md:grid-cols-2 gap-12 items-center">
          <motion.div
            initial={{ opacity: 0, x: -50 }}
            animate={inView ? { opacity: 1, x: 0 } : {}}
            className="space-y-6"
          >
            {[
              { icon: Search, title: "Search Query", desc: "User searches for information" },
              { icon: Database, title: "Cache Check", desc: "System checks cached versions" },
              { icon: Archive, title: "Archive Scan", desc: "Wayback Machine retrieval" },
              { icon: Globe, title: "Result Display", desc: "Old data appears in results" }
            ].map((step, i) => (
              <motion.div
                key={i}
                initial={{ opacity: 0, x: -30 }}
                animate={inView ? { opacity: 1, x: 0 } : {}}
                transition={{ delay: i * 0.2 }}
                className="flex items-center gap-4 bg-dark-card p-4 rounded-lg cyber-border"
              >
                <step.icon className="text-cyber-blue w-8 h-8" />
                <div>
                  <h3 className="font-bold text-lg">{step.title}</h3>
                  <p className="text-gray-400">{step.desc}</p>
                </div>
              </motion.div>
            ))}
          </motion.div>

          <motion.div
            initial={{ opacity: 0, scale: 0.8 }}
            animate={inView ? { opacity: 1, scale: 1 } : {}}
            className="relative"
          >
            <div className="bg-dark-card p-8 rounded-lg cyber-border">
              <div className="text-center mb-6">
                <Search className="text-cyber-blue w-16 h-16 mx-auto mb-4" />
                <h3 className="text-2xl font-bold">Search Simulation</h3>
              </div>
              <div className="space-y-4">
                <div className="bg-gray-800 p-3 rounded border-l-4 border-green-400">
                  <p className="text-sm text-green-400">âœ“ Current website</p>
                </div>
                <div className="bg-gray-800 p-3 rounded border-l-4 border-yellow-400">
                  <p className="text-sm text-yellow-400">âš  Cached version</p>
                </div>
                <div className="bg-gray-800 p-3 rounded border-l-4 border-red-400">
                  <p className="text-sm text-red-400">ðŸ—„ Archived snapshot</p>
                </div>
              </div>
            </div>
          </motion.div>
        </div>
      </div>
    </section>
  );
};

export default DataRetrievalSection;
