import React from 'react';
import { motion } from 'framer-motion';
import { Shield, Eye, Lock, Trash2, Settings, UserCheck } from 'lucide-react';

const SafetyTips = () => {
  const tips = [
    { icon: Eye, title: "Think Before Posting", desc: "Consider long-term impact of every share" },
    { icon: Lock, title: "Use Privacy Settings", desc: "Limit who can see your content" },
    { icon: Shield, title: "Regular Privacy Audits", desc: "Review your digital footprint monthly" },
    { icon: Trash2, title: "Delete Old Content", desc: "Remove outdated posts and photos" },
    { icon: Settings, title: "Manage Data Settings", desc: "Control how platforms use your data" },
    { icon: UserCheck, title: "Google Yourself", desc: "Monitor what others can find about you" }
  ];

  return (
    <section id="safety" className="py-20 px-4">
      <div className="max-w-7xl mx-auto">
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          whileInView={{ opacity: 1, y: 0 }}
          className="text-center mb-16"
        >
          <h2 className="text-5xl font-bold mb-6 glow-text">
            Stay <span className="text-cyber-blue">Safe</span> Online
          </h2>
          <p className="text-xl text-gray-300">Protect your digital future with these essential tips</p>
        </motion.div>

        <div className="grid md:grid-cols-3 gap-8">
          {tips.map((tip, index) => (
            <motion.div
              key={index}
              initial={{ opacity: 0, y: 50 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ delay: index * 0.1 }}
              whileHover={{ scale: 1.05, rotateY: 5 }}
              className="bg-dark-card p-6 rounded-lg cyber-border hover-glow text-center"
            >
              <tip.icon className="text-cyber-blue w-12 h-12 mx-auto mb-4" />
              <h3 className="text-xl font-bold mb-3">{tip.title}</h3>
              <p className="text-gray-300">{tip.desc}</p>
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default SafetyTips;
