import React from 'react';
import { motion } from 'framer-motion';
import { AlertCircle, TrendingUp, Users, Shield } from 'lucide-react';

const EducationSection = () => {
  const cases = [
    {
      icon: TrendingUp,
      title: "Viral Post Consequences",
      description: "A single tweet can resurface years later, affecting career opportunities and personal relationships.",
      impact: "High",
      color: "border-red-500"
    },
    {
      icon: Users,
      title: "Data Breach Exposure",
      description: "Personal information from breached databases continues to circulate on the dark web indefinitely.",
      impact: "Critical",
      color: "border-red-600"
    },
    {
      icon: Shield,
      title: "Digital Reputation Damage",
      description: "Negative content can dominate search results, overshadowing positive achievements.",
      impact: "Medium",
      color: "border-yellow-500"
    },
    {
      icon: AlertCircle,
      title: "Cached Content Persistence",
      description: "Even after deletion, cached versions remain accessible through search engines and archives.",
      impact: "High",
      color: "border-orange-500"
    }
  ];

  return (
    <section id="education" className="py-20 px-4 bg-gradient-to-b from-gray-900 to-dark-bg">
      <div className="max-w-7xl mx-auto">
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          whileInView={{ opacity: 1, y: 0 }}
          className="text-center mb-16"
        >
          <h2 className="text-5xl font-bold mb-6 glow-text">
            Why It <span className="text-cyber-pink">Matters</span>
          </h2>
          <p className="text-xl text-gray-300 max-w-3xl mx-auto">
            Real-world consequences of digital permanence that affect millions of people worldwide
          </p>
        </motion.div>

        <div className="grid md:grid-cols-2 gap-8">
          {cases.map((case_, index) => (
            <motion.div
              key={index}
              initial={{ opacity: 0, y: 50 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ delay: index * 0.2 }}
              whileHover={{ scale: 1.02, y: -5 }}
              className={`bg-dark-card p-6 rounded-lg border-l-4 ${case_.color} hover-glow cursor-pointer`}
            >
              <div className="flex items-start gap-4">
                <case_.icon className="text-cyber-blue w-8 h-8 mt-1" />
                <div className="flex-1">
                  <div className="flex justify-between items-start mb-3">
                    <h3 className="text-xl font-bold">{case_.title}</h3>
                    <span className={`px-2 py-1 rounded text-xs font-bold ${
                      case_.impact === 'Critical' ? 'bg-red-600' :
                      case_.impact === 'High' ? 'bg-orange-600' : 'bg-yellow-600'
                    }`}>
                      {case_.impact}
                    </span>
                  </div>
                  <p className="text-gray-300">{case_.description}</p>
                </div>
              </div>
            </motion.div>
          ))}
        </div>

        <motion.div
          initial={{ opacity: 0 }}
          whileInView={{ opacity: 1 }}
          transition={{ delay: 1 }}
          className="mt-16 text-center"
        >
          <div className="bg-dark-card p-8 rounded-lg cyber-border max-w-4xl mx-auto">
            <AlertCircle className="text-yellow-400 w-16 h-16 mx-auto mb-4" />
            <h3 className="text-2xl font-bold mb-4 text-yellow-400">Remember</h3>
            <p className="text-lg text-gray-300">
              Every digital action creates a permanent record. What you share today 
              becomes part of your digital legacy forever.
            </p>
          </div>
        </motion.div>
      </div>
    </section>
  );
};

export default EducationSection;
