import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { Search, AlertTriangle } from 'lucide-react';

const GoogleSimulation = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [results, setResults] = useState([]);
  const [isSearching, setIsSearching] = useState(false);

  const mockResults = [
    { type: 'social', title: 'Facebook Profile', url: 'facebook.com/profile', snippet: 'Public posts and photos from 2019-2024' },
    { type: 'cached', title: 'Cached LinkedIn Page', url: 'webcache.googleusercontent.com', snippet: 'Cached version from last month' },
    { type: 'archive', title: 'Wayback Machine Snapshot', url: 'web.archive.org', snippet: 'Archived page from 2020' },
    { type: 'news', title: 'News Article Mention', url: 'localnews.com', snippet: 'Mentioned in community event coverage' }
  ];

  const handleSearch = () => {
    if (!searchTerm.trim()) return;
    setIsSearching(true);
    setTimeout(() => {
      setResults(mockResults);
      setIsSearching(false);
    }, 2000);
  };

  return (
    <section id="simulation" className="py-20 px-4">
      <div className="max-w-4xl mx-auto">
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          whileInView={{ opacity: 1, y: 0 }}
          className="text-center mb-12"
        >
          <h2 className="text-5xl font-bold mb-6 glow-text">
            <span className="text-cyber-pink">Google Yourself</span> Simulation
          </h2>
          <p className="text-xl text-gray-300">See what the internet remembers about you</p>
        </motion.div>

        <div className="bg-dark-card p-8 rounded-lg cyber-border">
          <div className="flex gap-4 mb-8">
            <input
              type="text"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              placeholder="Enter a name to search..."
              className="flex-1 bg-gray-800 border border-gray-600 rounded-lg px-4 py-3 text-white focus:border-cyber-blue focus:outline-none"
              onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
            />
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={handleSearch}
              disabled={isSearching}
              className="px-6 py-3 bg-cyber-blue text-black rounded-lg font-bold hover:bg-cyan-400 disabled:opacity-50"
            >
              {isSearching ? (
                <motion.div animate={{ rotate: 360 }} transition={{ duration: 1, repeat: Infinity, ease: "linear" }}>
                  <Search size={20} />
                </motion.div>
              ) : (
                <Search size={20} />
              )}
            </motion.button>
          </div>

          {results.length > 0 && (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              className="space-y-4"
            >
              <div className="flex items-center gap-2 text-yellow-400 mb-4">
                <AlertTriangle size={20} />
                <p className="font-bold">Warning: Think before you post â€“ the internet never forgets!</p>
              </div>

              {results.map((result, index) => (
                <motion.div
                  key={index}
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: index * 0.1 }}
                  className="bg-gray-800 p-4 rounded-lg border-l-4 border-cyber-blue"
                >
                  <h3 className="text-cyber-blue font-bold mb-1">{result.title}</h3>
                  <p className="text-green-400 text-sm mb-2">{result.url}</p>
                  <p className="text-gray-300">{result.snippet}</p>
                </motion.div>
              ))}
            </motion.div>
          )}
        </div>
      </div>
    </section>
  );
};

export default GoogleSimulation;
